/*
 * Initialize an instance of object `directory_popup'.
 */
faxtool_directory_popup_objects *
faxtool_directory_popup_objects_initialize(faxtool_directory_popup_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (faxtool_directory_popup_objects *) calloc(1, sizeof (faxtool_directory_popup_objects))))
		return (faxtool_directory_popup_objects *) NULL;
	if (!ip->directory_popup)
		ip->directory_popup = faxtool_directory_popup_directory_popup_create(ip, owner);
	if (!ip->directory_controls)
		ip->directory_controls = faxtool_directory_popup_directory_controls_create(ip, ip->directory_popup);
	if (!ip->directory_list)
		ip->directory_list = faxtool_directory_popup_directory_list_create(ip, ip->directory_controls);
	if (!ip->dir_add_button)
		ip->dir_add_button = faxtool_directory_popup_dir_add_button_create(ip, ip->directory_controls);
	if (!ip->dir_change_button)
		ip->dir_change_button = faxtool_directory_popup_dir_change_button_create(ip, ip->directory_controls);
	if (!ip->dir_delete_button)
		ip->dir_delete_button = faxtool_directory_popup_dir_delete_button_create(ip, ip->directory_controls);
	if (!ip->dir_buttons)
		ip->dir_buttons = faxtool_directory_popup_dir_buttons_create(ip, ip->directory_controls);
	if (!ip->dir_list_objects)
		ip->dir_list_objects = faxtool_directory_popup_dir_list_objects_create(ip, ip->directory_controls);
	if (!ip->dir_name)
		ip->dir_name = faxtool_directory_popup_dir_name_create(ip, ip->directory_controls);
	if (!ip->dir_company)
		ip->dir_company = faxtool_directory_popup_dir_company_create(ip, ip->directory_controls);
	if (!ip->dir_fax)
		ip->dir_fax = faxtool_directory_popup_dir_fax_create(ip, ip->directory_controls);
	if (!ip->dir_fields)
		ip->dir_fields = faxtool_directory_popup_dir_fields_create(ip, ip->directory_controls);
	if (!ip->dir_select_button)
		ip->dir_select_button = faxtool_directory_popup_dir_select_button_create(ip, ip->directory_controls);
	if (!ip->dir_save_button)
		ip->dir_save_button = faxtool_directory_popup_dir_save_button_create(ip, ip->directory_controls);
	if (!ip->dir_lower_left_buttons)
		ip->dir_lower_left_buttons = faxtool_directory_popup_dir_lower_left_buttons_create(ip, ip->directory_controls);
	if (!ip->dir_clear_button)
		ip->dir_clear_button = faxtool_directory_popup_dir_clear_button_create(ip, ip->directory_controls);
	if (!ip->dir_reset_button)
		ip->dir_reset_button = faxtool_directory_popup_dir_reset_button_create(ip, ip->directory_controls);
	if (!ip->dir_lower_right_buttons)
		ip->dir_lower_right_buttons = faxtool_directory_popup_dir_lower_right_buttons_create(ip, ip->directory_controls);
	if (!ip->dir_lower_buttons)
		ip->dir_lower_buttons = faxtool_directory_popup_dir_lower_buttons_create(ip, ip->directory_controls);
	if (!ip->dir_objects)
		ip->dir_objects = faxtool_directory_popup_dir_objects_create(ip, ip->directory_controls);
	window_fit(ip->directory_controls);
	
	window_fit(ip->directory_popup);
	
	xv_set(ip->directory_controls,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

