/*
 * Initialize an instance of object `prop_popup'.
 */
faxtool_prop_popup_objects *
faxtool_prop_popup_objects_initialize(faxtool_prop_popup_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (faxtool_prop_popup_objects *) calloc(1, sizeof (faxtool_prop_popup_objects))))
		return (faxtool_prop_popup_objects *) NULL;
	if (!ip->prop_popup)
		ip->prop_popup = faxtool_prop_popup_prop_popup_create(ip, owner);
	if (!ip->prop_controls)
		ip->prop_controls = faxtool_prop_popup_prop_controls_create(ip, ip->prop_popup);
	if (!ip->fax_host)
		ip->fax_host = faxtool_prop_popup_fax_host_create(ip, ip->prop_controls);
	if (!ip->fax_dir)
		ip->fax_dir = faxtool_prop_popup_fax_dir_create(ip, ip->prop_controls);
	if (!ip->prop_upper_fields)
		ip->prop_upper_fields = faxtool_prop_popup_prop_upper_fields_create(ip, ip->prop_controls);
	if (!ip->view_cmd)
		ip->view_cmd = faxtool_prop_popup_view_cmd_create(ip, ip->prop_controls);
	if (!ip->print_cmd)
		ip->print_cmd = faxtool_prop_popup_print_cmd_create(ip, ip->prop_controls);
	if (!ip->preview_cmd)
		ip->preview_cmd = faxtool_prop_popup_preview_cmd_create(ip, ip->prop_controls);
	if (!ip->prop_lower_fields)
		ip->prop_lower_fields = faxtool_prop_popup_prop_lower_fields_create(ip, ip->prop_controls);
	if (!ip->prop_save_button)
		ip->prop_save_button = faxtool_prop_popup_prop_save_button_create(ip, ip->prop_controls);
	if (!ip->prop_apply_button)
		ip->prop_apply_button = faxtool_prop_popup_prop_apply_button_create(ip, ip->prop_controls);
	if (!ip->prop_left_buttons)
		ip->prop_left_buttons = faxtool_prop_popup_prop_left_buttons_create(ip, ip->prop_controls);
	if (!ip->prop_clear_button)
		ip->prop_clear_button = faxtool_prop_popup_prop_clear_button_create(ip, ip->prop_controls);
	if (!ip->prop_reset_button)
		ip->prop_reset_button = faxtool_prop_popup_prop_reset_button_create(ip, ip->prop_controls);
	if (!ip->prop_right_buttons)
		ip->prop_right_buttons = faxtool_prop_popup_prop_right_buttons_create(ip, ip->prop_controls);
	if (!ip->prop_lower_buttons)
		ip->prop_lower_buttons = faxtool_prop_popup_prop_lower_buttons_create(ip, ip->prop_controls);
	if (!ip->prop_objects)
		ip->prop_objects = faxtool_prop_popup_prop_objects_create(ip, ip->prop_controls);
	window_fit(ip->prop_controls);
	
	window_fit(ip->prop_popup);
	
	xv_set(ip->prop_controls,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

