void
warning(s)
char *s;
{
	/* notice_prompt( ... */
	fprintf(stderr, "%s\n", s);
}

void
error(s)
char *s;
{
	warning(s);
	if (Changed)
		writeObjs("genix.hup");
	exit(1);
}

